USE sql11406633;

DROP TABLE IF EXISTS Friendslist;
DROP TABLE IF EXISTS Profilepage;
DROP TABLE IF EXISTS Authentication;
DROP TABLE IF EXISTS Gym;
DROP TRIGGER IF EXISTS set_default_insert;
DROP TRIGGER IF EXISTS set_default_update;

CREATE TABLE Authentication (
	USERNAME VARCHAR(255) NOT NULL PRIMARY KEY,
	PASS_HASH VARCHAR(255) NOT NULL,
	USER_ROLE INTEGER NOT NULL
);

CREATE TABLE Gym (
	ID INTEGER NOT NULL AUTO_INCREMENT,
	NAME VARCHAR(255) NOT NULL,
	STREET VARCHAR(255) NOT NULL,
	SNUM VARCHAR(5) NOT NULL,
	POSTAL_CODE VARCHAR(10) NOT NULL,
	CITY VARCHAR(255) NOT NULL,
	PROVINCE VARCHAR(255) NOT NULL,
	COUNTRY VARCHAR(255) NOT NULL,
	PRIMARY KEY(ID)
);

CREATE TABLE Profilepage (
	USERNAME VARCHAR(255) PRIMARY KEY,
	PICTURE_HASH MEDIUMTEXT,
	BIO MEDIUMTEXT,
	MAIN_GYM INTEGER,
	FOREIGN KEY (USERNAME) REFERENCES Authentication(USERNAME) ON UPDATE RESTRICT ON DELETE CASCADE,
	FOREIGN KEY (MAIN_GYM) REFERENCES Gym(ID) ON UPDATE RESTRICT ON DELETE CASCADE
);

CREATE TABLE Friendslist (
	ID1 VARCHAR(255) NOT NULL,
	ID2 VARCHAR(255) NOT NULL,
	PRIMARY KEY (ID1, ID2),
	FOREIGN KEY (ID1) REFERENCES Profilepage(USERNAME) ON UPDATE RESTRICT ON DELETE CASCADE,
	FOREIGN KEY (ID2) REFERENCES Profilepage(USERNAME) ON UPDATE RESTRICT ON DELETE CASCADE
);

DELIMITER $$
CREATE TRIGGER set_default_insert BEFORE INSERT ON Profilepage
FOR EACH ROW
BEGIN
  IF NEW.PICTURE_HASH IS NULL THEN
	SET NEW.PICTURE_HASH = '';
  END IF;
END$$

CREATE TRIGGER set_default_update BEFORE UPDATE ON Profilepage
FOR EACH ROW
BEGIN
  IF NEW.PICTURE_HASH IS NULL THEN
	SET NEW.PICTURE_HASH = '';
  END IF;
END$$

DELIMITER ;

